      {/* Deal Selector */}
      <Card className="p-6">
        <Label>Select Deal</Label>
        <Select value={selectedDeal} onValueChange={setSelectedDeal}>
          <SelectTrigger className="mt-2">
            <SelectValue placeholder="Choose a deal to manage files" />
          </SelectTrigger>
          <SelectContent>
            {deals?.map((deal) => (
              <SelectItem key={deal.id} value={deal.id}>
                {deal.title}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </Card>

      {selectedDeal && (
        <>
