const FileUploadForm = ({
  dealId,
  folders,
  onSuccess,
}: {
  dealId: string;
  folders: any[];
  onSuccess: () => void;
}) => {
  const [file, setFile] = useState<File | null>(null);
  const [folderId, setFolderId] = useState<string>("");

  const uploadMutation = useMutation({
    mutationFn: async () => {
      if (!file) throw new Error("No file selected");

      // Generate unique storage path
      const timestamp = Date.now();
      const safeName = file.name.replace(/[^a-zA-Z0-9.-]/g, "_");
      const storagePath = `${dealId}/${timestamp}_${safeName}`;

      // Upload to storage
      const { error: uploadError } = await supabase.storage
        .from("data-room")
        .upload(storagePath, file);

      if (uploadError) throw uploadError;

      // Save to database
      const { error: dbError } = await supabase.from("files").insert({
        deal_id: dealId,
        folder_id: folderId || null,
        name: file.name,
        storage_path: storagePath,
        file_type: file.type,
      });

      if (dbError) throw dbError;
    },
    onSuccess: () => {
      toast.success("File uploaded");
      onSuccess();
    },
    onError: () => {
      toast.error("Failed to upload file");
    },
  });

  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        uploadMutation.mutate();
      }}
      className="space-y-4"
    >
      <div className="space-y-2">
        <Label htmlFor="folder">Folder (optional)</Label>
        <Select value={folderId} onValueChange={setFolderId}>
          <SelectTrigger>
            <SelectValue placeholder="Root folder" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="">Root folder</SelectItem>
            {folders.map((folder) => (
              <SelectItem key={folder.id} value={folder.id}>
                {folder.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="space-y-2">
        <Label htmlFor="file">File *</Label>
        <Input
          id="file"
          type="file"
          onChange={(e) => setFile(e.target.files?.[0] || null)}
          required
        />
        <p className="text-xs text-muted-foreground">Max file size: 20MB</p>
      </div>

      <Button type="submit" className="w-full" disabled={uploadMutation.isPending || !file}>
        {uploadMutation.isPending ? "Uploading..." : "Upload File"}
      </Button>
    </form>
  );
};

export default FileManager;

