  });

  const updateProjectMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const { error } = await supabase.from("ip_projects").update({ status }).eq("id", id);
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["ip-projects"] });
      toast.success("Project status updated");
    },
    onError: (error) => toast.error(error.message),
  });

  const addTaskMutation = useMutation({
    mutationFn: async (task: typeof newTask & { project_id: string }) => {
      const { error } = await supabase.from("ip_tasks").insert(task);
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["ip-tasks"] });
      toast.success("Task added");
      setIsTaskDialogOpen(false);
      setNewTask({ title: "", description: "", priority: "medium" });
    },
    onError: (error) => toast.error(error.message),
  });

  const updateTaskMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      const { error } = await supabase
        .from("ip_tasks")
        .update({ 
          status, 
          completed_at: status === "done" ? new Date().toISOString() : null 
        })
        .eq("id", id);
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["ip-tasks"] });
      toast.success("Task updated");
    },
    onError: (error) => toast.error(error.message),
  });

  const filteredProjects = projects?.filter(
    (p) => selectedType === "all" || p.type === selectedType
  );

  const getTasksForProject = (projectId: string) =>
    tasks?.filter((t) => t.project_id === projectId) || [];

  const getProjectStats = () => {
    if (!projects) return { patents: 0, trademarks: 0, copyrights: 0, total: 0 };
    return {
      patents: projects.filter((p) => p.type === "patent").length,
      trademarks: projects.filter((p) => p.type === "trademark").length,
      copyrights: projects.filter((p) => p.type === "copyright").length,
      total: projects.length,
    };
  };

  const stats = getProjectStats();

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-4">
            <div className="text-2xl font-bold">{stats.patents}</div>
            <p className="text-sm text-muted-foreground">Patents</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-4">
            <div className="text-2xl font-bold">{stats.trademarks}</div>
            <p className="text-sm text-muted-foreground">Trademarks</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-4">
            <div className="text-2xl font-bold">{stats.copyrights}</div>
            <p className="text-sm text-muted-foreground">Copyrights</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-4">
            <div className="text-2xl font-bold">{stats.total}</div>
            <p className="text-sm text-muted-foreground">Total</p>
          </CardContent>
        </Card>
      </div>

      {/* Controls */}
      <div className="flex flex-col sm:flex-row gap-4 justify-between">
        <Select value={selectedType} onValueChange={setSelectedType}>
          <SelectTrigger className="w-[180px]">
            <SelectValue placeholder="Filter by type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="patent">Patents</SelectItem>
            <SelectItem value="trademark">Trademarks</SelectItem>
            <SelectItem value="copyright">Copyrights</SelectItem>
          </SelectContent>
        </Select>

        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add IP Project
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add IP Project</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <Select
                value={newProject.type}
                onValueChange={(v) => setNewProject({ ...newProject, type: v as "patent" | "trademark" | "copyright" })}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="patent">Patent</SelectItem>
                  <SelectItem value="trademark">Trademark</SelectItem>
                  <SelectItem value="copyright">Copyright</SelectItem>
                </SelectContent>
              </Select>
              <Input
                placeholder="Title"
                value={newProject.title}
                onChange={(e) => setNewProject({ ...newProject, title: e.target.value })}
              />
              <Textarea
                placeholder="Description"
                value={newProject.description}
                onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
              />
              <Select
                value={newProject.priority}
                onValueChange={(v) => setNewProject({ ...newProject, priority: v })}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low Priority</SelectItem>
                  <SelectItem value="medium">Medium Priority</SelectItem>
                  <SelectItem value="high">High Priority</SelectItem>
                  <SelectItem value="critical">Critical</SelectItem>
                </SelectContent>
              </Select>
              <Button
                onClick={() => addProjectMutation.mutate(newProject)}
                disabled={!newProject.title || addProjectMutation.isPending}
                className="w-full"
              >
                Create Project
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Projects List */}
      <Accordion type="multiple" className="space-y-4">
        {filteredProjects?.map((project) => {
          const Icon = typeIcons[project.type];
          const projectTasks = getTasksForProject(project.id);
          const completedTasks = projectTasks.filter((t) => t.status === "done").length;

          return (
            <AccordionItem
              key={project.id}
              value={project.id}
              className={`border-l-4 ${priorityColors[project.priority]} rounded-lg overflow-hidden`}
            >
              <Card className="border-0">
                <AccordionTrigger className="px-6 py-4 hover:no-underline">
                  <div className="flex items-center gap-4 flex-1">
                    <Icon className="h-5 w-5 text-muted-foreground" />
                    <div className="flex-1 text-left">
                      <h3 className="font-semibold">{project.title}</h3>
                      <p className="text-sm text-muted-foreground line-clamp-1">
                        {project.description}
                      </p>
                    </div>
                    <Badge className={statusColors[project.status]}>{project.status}</Badge>
                    <span className="text-sm text-muted-foreground">
                      {completedTasks}/{projectTasks.length} tasks
                    </span>
                  </div>
                </AccordionTrigger>
                <AccordionContent>
                  <CardContent className="pt-0 space-y-4">
                    {/* Status Update */}
                    <div className="flex gap-2 flex-wrap">
                      {["draft", "in_progress", "filed", project.type === "trademark" ? "registered" : "granted"].map(
                        (status) => (
                          <Button
                            key={status}
                            variant={project.status === status ? "default" : "outline"}
                            size="sm"
                            onClick={() => updateProjectMutation.mutate({ id: project.id, status })}
                          >
                            {status.replace("_", " ")}
                          </Button>
                        )
                      )}
                    </div>

                    {/* Tasks */}
                    <div className="space-y-2">
                      <div className="flex justify-between items-center">
                        <h4 className="font-medium">Tasks</h4>
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={() => {
                            setSelectedProjectId(project.id);
                            setIsTaskDialogOpen(true);
                          }}
                        >
                          <Plus className="h-4 w-4 mr-1" />
                          Add Task
                        </Button>
                      </div>
                      {projectTasks.length === 0 ? (
                        <p className="text-sm text-muted-foreground">No tasks yet</p>
                      ) : (
                        <div className="space-y-2">
                          {projectTasks.map((task) => (
                            <div
                              key={task.id}
                              className="flex items-center gap-3 p-2 rounded-md bg-muted/50"
                            >
                              <Button
                                variant="ghost"
                                size="sm"
                                className="h-6 w-6 p-0"
                                onClick={() =>
                                  updateTaskMutation.mutate({
                                    id: task.id,
                                    status: task.status === "done" ? "todo" : "done",
                                  })
                                }
                              >
                                {task.status === "done" ? (
                                  <CheckCircle2 className="h-4 w-4 text-green-500" />
                                ) : (
                                  <Clock className="h-4 w-4 text-muted-foreground" />
                                )}
                              </Button>
                              <span
                                className={
                                  task.status === "done"
                                    ? "line-through text-muted-foreground"
                                    : ""
                                }
                              >
                                {task.title}
                              </span>
                              <Badge variant="outline" className="ml-auto text-xs">
                                {task.priority}
                              </Badge>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </CardContent>
                </AccordionContent>
              </Card>
            </AccordionItem>
          );
        })}
      </Accordion>

      {/* Add Task Dialog */}
      <Dialog open={isTaskDialogOpen} onOpenChange={setIsTaskDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Task</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <Input
              placeholder="Task title"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            />
            <Textarea
              placeholder="Description (optional)"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
            />
            <Select
              value={newTask.priority}
              onValueChange={(v) => setNewTask({ ...newTask, priority: v })}
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="low">Low Priority</SelectItem>
                <SelectItem value="medium">Medium Priority</SelectItem>
                <SelectItem value="high">High Priority</SelectItem>
                <SelectItem value="critical">Critical</SelectItem>
              </SelectContent>
            </Select>
            <Button
              onClick={() =>
                selectedProjectId &&
                addTaskMutation.mutate({ ...newTask, project_id: selectedProjectId })
              }
              disabled={!newTask.title || addTaskMutation.isPending}
              className="w-full"
            >
              Add Task
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useEffect, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import { Card } from '@/components/ui/card';
import { Download, Sparkles, Lock, Loader2, Keyboard } from 'lucide-react';
import { VirtualKeyboard } from '@/components/qbc/VirtualKeyboard';
import { supabase } from '@/integrations/supabase/client';
import { useLattices } from '@/hooks/useLattices';
import { encodeText } from '@/lib/qbc/encoder';
import { encodeText3D } from '@/lib/qbc/encoder3d';
import { renderSvg } from '@/lib/qbc/renderer2d';
import { get3DLattice } from '@/lib/qbc/lattices3d';
import { Glyph3DRenderer } from '@/components/qbc/Glyph3DRenderer';
import { useGlyphClaim } from '@/hooks/useGlyphClaim';
import { useNavigate } from 'react-router-dom';
import { toast } from 'sonner';
import {
  EncodedPath,
  GlyphStyle,
  GlyphOrientation,
  LatticeAnchors2D,
  LatticeRules,
  DEFAULT_STYLE,
  DEFAULT_ORIENTATION
} from '@/lib/qbc/types';
import { EncodedPath3D } from '@/lib/qbc/types3d';

const HomeSimulator = () => {
  const navigate = useNavigate();
  const { lattices, loading: latticesLoading } = useLattices();
  const { claimGlyph, claiming } = useGlyphClaim();

  const [text, setText] = useState('');
  const [selectedLatticeId, setSelectedLatticeId] = useState<string>('');
  const [mode, setMode] = useState<'2d' | '3d'>('2d');
  const [lattice3DType, setLattice3DType] = useState<'7x7x7' | 'metatron'>('7x7x7');
  const [scriptFilter, setScriptFilter] = useState<'all' | 'latin' | 'cyrillic' | 'greek' | 'hebrew' | 'cjk' | 'arabic'>('all');
  const [path, setPath] = useState<EncodedPath | null>(null);
  const [path3D, setPath3D] = useState<EncodedPath3D | null>(null);
  const [user, setUser] = useState<any>(null);
  const [generating, setGenerating] = useState(false);
  const [showLabels, setShowLabels] = useState(false);
  const [showAnchors, setShowAnchors] = useState(true);
  const [showKeyboard, setShowKeyboard] = useState(false);

  // Script filter patterns
  const scriptPatterns: Record<string, RegExp> = {
    all: /./,
    latin: /^[A-Za-z0-9\s.,!?'"()\-:;@#$%&*+=\/\\<>]+$/,
    cyrillic: /^[Ð-Ð¯Ð°-ÑÐÑ0-9\s.,!?'"()\-:;]+$/,
    greek: /^[Î-Î©Î±-Ï0-9\s.,!?'"()\-:;]+$/,
    hebrew: /^[\u0590-\u05FF0-9\s.,!?'"()\-:;]+$/,
    cjk: /^[\u4E00-\u9FFF\u3400-\u4DBF0-9\s.,!?'"()\-:;]+$/,
    arabic: /^[\u0600-\u06FF0-9\s.,!?'"()\-:;]+$/,
  };

  const scriptLabels: Record<string, string> = {
    all: 'All Scripts',
    latin: 'Latin (A-Z)',
    cyrillic: 'Cyrillic (Ð-Ð¯)',
    greek: 'Greek (Î-Î©)',
    hebrew: 'Hebrew (×-×ª)',
    cjk: 'CJK / Mandarin',
    arabic: 'Arabic (Ø§-Ù)',
  };

  // White background, black lines for default
  const style: GlyphStyle = useMemo(() => ({
    ...DEFAULT_STYLE,
    strokeColor: '#000000',
    nodeColor: '#000000',
    nodeFillColor: '#ffffff',
    backgroundColor: '#ffffff',
    gridColor: '#e5e5e5',
    showNodes: showAnchors,
    showGrid: false,
    showLabels: showLabels,
    strokeWidth: 2,
    nodeSize: 6,
  }), [showLabels, showAnchors]);

  const orientation: GlyphOrientation = DEFAULT_ORIENTATION;

  // Get current user
  useEffect(() => {
    supabase.auth.getUser().then(({ data: { user } }) => setUser(user));

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  // Set default lattice
  useEffect(() => {
    if (lattices.length > 0 && !selectedLatticeId) {
      const defaultLattice = lattices.find(l => l.is_default) || lattices[0];
      setSelectedLatticeId(defaultLattice.id);
    }
  }, [lattices, selectedLatticeId]);

  const selectedLattice = useMemo(() => 
    lattices.find(l => l.id === selectedLatticeId),
    [lattices, selectedLatticeId]
  );

  const handleGenerate = () => {
    if (!text.trim()) return;

    setGenerating(true);

    try {
      if (mode === '2d') {
        if (!selectedLattice) return;
        const anchors = selectedLattice.anchors_json as LatticeAnchors2D;
        const rules = selectedLattice.rules_json as LatticeRules;
        const encoded = encodeText(text, anchors, rules);
        setPath(encoded);
        setPath3D(null);
      } else {
        // 3D mode uses generated lattices
        const anchors3D = get3DLattice(lattice3DType);
        const encoded3D = encodeText3D(text, anchors3D, { 
          enableTick: true, 
          tickLengthFactor: 0.08, 
          insideBoundaryPreference: true, 
          nodeSpacing: 0.2 
        });
        setPath3D(encoded3D);
        setPath(null);
      }
    } catch (err) {
      console.error('Encoding error:', err);
      toast.error('Failed to generate glyph');
    } finally {
      setGenerating(false);
    }
  };

  const svgContent = useMemo(() => {
    if (!path || !selectedLattice || mode !== '2d') return null;
    const anchors = selectedLattice.anchors_json as LatticeAnchors2D;
    return renderSvg(path, anchors, style, orientation);
  }, [path, selectedLattice, style, orientation, mode]);

  const handleDownloadSvg = () => {
    if (!user) {
      toast.error('Sign in required to download glyphs');
      navigate('/auth');
      return;
    }
    if (!svgContent) return;
    const blob = new Blob([svgContent], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `qbc-${text.trim().toLowerCase().replace(/\s+/g, '-')}.svg`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleDownloadPng = () => {
    if (!user) {
      toast.error('Sign in required to download glyphs');
      navigate('/auth');
      return;
    }
    if (!svgContent) return;

    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      canvas.width = 800;
      canvas.height = 800;
      ctx?.drawImage(img, 0, 0, 800, 800);
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = `qbc-${text.trim().toLowerCase().replace(/\s+/g, '-')}.png`;
          a.click();
          URL.revokeObjectURL(url);
        }
      }, 'image/png');
    };

    img.src = `data:image/svg+xml;base64,${btoa(svgContent)}`;
  };

  const handleClaim = async () => {
    if (!user) {
      navigate('/auth');
      return;
    }

    if (!path || !selectedLattice || !svgContent) return;

    const result = await claimGlyph(
      text,
      selectedLattice.id,
      selectedLattice.version,
      selectedLattice.rules_json as LatticeRules,
      path,
      orientation,
      style,
      svgContent
    );

    if (result.success && result.claimId) {
      navigate(`/glyph/${result.claimId}`);
    } else if (result.alreadyClaimed) {
      toast.error('This glyph has already been claimed');
      if (result.existingClaimId) {
        navigate(`/glyph/${result.existingClaimId}`);
      }
    } else {
      toast.error(result.error || 'Failed to claim glyph');
    }
  };

  return (
    <section className="py-8 md:py-12">
      <div className="container px-4 md:px-6">
        <div className="max-w-4xl mx-auto">
          {/* Header */}
          <div className="text-center mb-6">
            <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-primary/10 text-primary text-sm font-mono mb-3">
              <Sparkles className="w-4 h-4" />
              QBC Simulator
            </div>
            <h2 className="text-2xl md:text-3xl font-display font-bold text-foreground mb-2">
              Generate Your Glyph
            </h2>
            <p className="text-muted-foreground text-sm md:text-base">
              Type a word, name, or phrase to create a unique geometric signature
            </p>
          </div>

          {/* Simulator Card */}
          <Card className="p-4 md:p-6 bg-card border-border">
            <div className="grid md:grid-cols-2 gap-6">
              {/* Controls */}
              <div className="space-y-4">
                {/* Text Input */}
                <div>
                  <Label htmlFor="glyph-text" className="text-sm font-medium mb-2 block">
                    Text to Encode
                  </Label>
                  <Input
                    id="glyph-text"
                    placeholder={scriptFilter === 'all' ? 'Type a word, name, or phrase...' : `Type in ${scriptLabels[scriptFilter]}...`}
                    value={text}
                    onChange={(e) => {
                      const val = e.target.value;
                      // Only validate if not 'all' and there's text
                      if (scriptFilter === 'all' || val === '' || scriptPatterns[scriptFilter].test(val)) {
                        setText(val);
                      }
                    }}
                    className="bg-background text-foreground"
                    maxLength={50}
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    {scriptFilter !== 'all' ? `${scriptLabels[scriptFilter]} characters only` : (mode === '3d' ? 'Latin, Cyrillic, Greek, Hebrew, CJK, Arabic + symbols' : 'A-Z and spaces only')} â¢ {text.length}/50
                  </p>
                </div>

                {/* Lattice Selector - contextual based on mode */}
                {mode === '2d' ? (
                  <div>
                    <Label className="text-sm font-medium mb-2 block">Lattice</Label>
                    <Select 
                      value={selectedLatticeId} 
                      onValueChange={setSelectedLatticeId}
                      disabled={latticesLoading}
                    >
                      <SelectTrigger className="bg-background">
                        <SelectValue placeholder="Select lattice..." />
                      </SelectTrigger>
                      <SelectContent>
                        {lattices.map((lattice) => (
                          <SelectItem key={lattice.id} value={lattice.id}>
                            {lattice.name} {lattice.is_default && '(Default)'}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                ) : (
                  <div>
                    <Label className="text-sm font-medium mb-2 block">3D Lattice Structure</Label>
                    <Select 
                      value={lattice3DType} 
                      onValueChange={(v) => setLattice3DType(v as '7x7x7' | 'metatron')}
                    >
                      <SelectTrigger className="bg-background">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="7x7x7">7Ã7Ã7 Cubic (343 anchors)</SelectItem>
                        <SelectItem value="metatron">Metatron's Cube</SelectItem>
                      </SelectContent>
                    </Select>
                    <p className="text-xs text-muted-foreground mt-1">
                      {lattice3DType === 'metatron' 
                        ? 'Sacred geometry with Platonic solid vertices' 
                        : 'Full alphanumeric + special characters'}
                    </p>
                  </div>
                )}

                {/* Script/Language Filter */}
                <div>
                  <div className="flex items-center justify-between mb-2">
                    <Label className="text-sm font-medium">Script / Language</Label>
                    {scriptFilter !== 'all' && scriptFilter !== 'latin' && (
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-7 px-2 text-xs"
                        onClick={() => setShowKeyboard(!showKeyboard)}
                      >
                        <Keyboard className="w-3 h-3 mr-1" />
                        {showKeyboard ? 'Hide' : 'Show'} Keyboard
                      </Button>
                    )}
                  </div>
                  <Select 
                    value={scriptFilter} 
                    onValueChange={(v) => {
                      setScriptFilter(v as typeof scriptFilter);
                      setText(''); // Clear text when changing script
                      // Auto-show keyboard for non-Latin scripts
                      if (v !== 'all' && v !== 'latin') {
                        setShowKeyboard(true);
                      } else {
                        setShowKeyboard(false);
                      }
                    }}
                  >
                    <SelectTrigger className="bg-background">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(scriptLabels).map(([key, label]) => (
                        <SelectItem key={key} value={key}>{label}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <p className="text-xs text-muted-foreground mt-1">
                    Filter character input to a specific script
                  </p>

                  {/* Virtual Keyboard */}
                  {showKeyboard && scriptFilter !== 'all' && scriptFilter !== 'latin' && (
                    <VirtualKeyboard
                      script={scriptFilter as 'cyrillic' | 'greek' | 'hebrew' | 'cjk' | 'arabic'}
                      onCharacter={(char) => {
                        if (text.length < 50) {
                          setText(prev => prev + char);
                        }
                      }}
                      onBackspace={() => setText(prev => prev.slice(0, -1))}
                      onClose={() => setShowKeyboard(false)}
                    />
                  )}
                </div>

                {/* Toggles Row */}
                <div className="flex flex-wrap items-center gap-4">
                  <div className="flex items-center gap-2">
                    <Switch
                      id="show-labels"
                      checked={showLabels}
                      onCheckedChange={setShowLabels}
                    />
