// QBC Composite Encoder
// Splits long text into chunks and creates positioned glyph cells

import { encodeText, normalizeText } from './encoder';
import { LatticeAnchors2D, LatticeRules, EncodedPath } from './types';
import { 
  GlyphCell, 
  CompositeGlyph, 
  CompositeMetadata, 
  ChunkConfig, 
  CompositeLayout,
  DEFAULT_CHUNK_CONFIG 
} from './compositeTypes';

/**
 * Generate a simple hash for the composite
 */
function generateCompositeHash(text: string): string {
  let hash = 0;
  const str = `${text}:${Date.now()}`;
  for (let i = 0; i < str.length; i++) {
    hash = ((hash << 5) - hash) + str.charCodeAt(i);
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16).padStart(8, '0');
}

/**
 * Split text into chunks of specified size
 */
export function chunkText(text: string, chunkSize: number): string[] {
  const normalized = normalizeText(text);
  const chunks: string[] = [];

  // Try to split on word boundaries when possible
  const words = normalized.split(' ');
  let currentChunk = '';

  for (const word of words) {
    if (word.length > chunkSize) {
      // Word is longer than chunk size, split it
      if (currentChunk) {
        chunks.push(currentChunk.trim());
        currentChunk = '';
      }
      for (let i = 0; i < word.length; i += chunkSize) {
        chunks.push(word.slice(i, i + chunkSize));
      }
    } else if ((currentChunk + ' ' + word).trim().length <= chunkSize) {
      currentChunk = (currentChunk + ' ' + word).trim();
    } else {
      if (currentChunk) {
        chunks.push(currentChunk);
      }
      currentChunk = word;
    }
  }

  if (currentChunk) {
    chunks.push(currentChunk);
  }

  return chunks.filter(c => c.length > 0);
}

/**
 * Calculate grid layout positions
 */
function calculateGridLayout(
  count: number, 
  columns: number
): { x: number; y: number; width: number; height: number }[] {
  const rows = Math.ceil(count / columns);
  const cellWidth = 1 / columns;
  const cellHeight = 1 / rows;
  const padding = 0.02;

  const positions: { x: number; y: number; width: number; height: number }[] = [];

  for (let i = 0; i < count; i++) {
    const col = i % columns;
    const row = Math.floor(i / columns);

    positions.push({
      x: col * cellWidth + padding,
      y: row * cellHeight + padding,
      width: cellWidth - padding * 2,
      height: cellHeight - padding * 2,
    });
  }

  return positions;
}

/**
 * Calculate hierarchical layout - large center, smaller around
 */
function calculateHierarchicalLayout(
  count: number,
  primaryScale: number
): { x: number; y: number; width: number; height: number }[] {
  const positions: { x: number; y: number; width: number; height: number }[] = [];

  if (count === 0) return positions;

  // Primary glyph in center
  positions.push({
    x: (1 - primaryScale) / 2,
    y: (1 - primaryScale) / 2,
    width: primaryScale,
    height: primaryScale,
  });

  if (count === 1) return positions;

  // Secondary glyphs around the edges
  const secondaryCount = count - 1;
  const secondaryScale = (1 - primaryScale) / 2 * 0.9;

  // Distribute around the primary
  const corners = [
    { x: 0, y: 0 }, // top-left
    { x: 1 - secondaryScale, y: 0 }, // top-right
    { x: 0, y: 1 - secondaryScale }, // bottom-left
    { x: 1 - secondaryScale, y: 1 - secondaryScale }, // bottom-right
  ];

  const edges = [
    { x: 0.5 - secondaryScale / 2, y: 0 }, // top
    { x: 0.5 - secondaryScale / 2, y: 1 - secondaryScale }, // bottom
    { x: 0, y: 0.5 - secondaryScale / 2 }, // left
    { x: 1 - secondaryScale, y: 0.5 - secondaryScale / 2 }, // right
  ];

  const allPositions = [...corners, ...edges];

  for (let i = 0; i < secondaryCount && i < allPositions.length; i++) {
    positions.push({
      x: allPositions[i].x,
      y: allPositions[i].y,
      width: secondaryScale,
      height: secondaryScale,
    });
  }

  return positions;
}

/**
 * Calculate mosaic layout with overlapping glyphs
 */
function calculateMosaicLayout(
  count: number,
  overlapFactor: number
): { x: number; y: number; width: number; height: number }[] {
  const positions: { x: number; y: number; width: number; height: number }[] = [];

  // Create a semi-random mosaic pattern
  const baseSize = Math.min(0.4, 1 / Math.sqrt(count));
  const sizeVariation = 0.15;

  // Seed for deterministic pseudo-random
  let seed = count * 12345;
  const pseudoRandom = () => {
    seed = (seed * 1103515245 + 12345) & 0x7fffffff;
    return seed / 0x7fffffff;
  };

  for (let i = 0; i < count; i++) {
    const sizeMult = 0.8 + pseudoRandom() * sizeVariation * 2;
    const size = baseSize * sizeMult;

    positions.push({
      x: pseudoRandom() * (1 - size),
      y: pseudoRandom() * (1 - size),
      width: size,
      height: size,
    });
  }

  return positions;
}

/**
 * Create glyph cells from chunks with layout positions
 */
export function createGlyphCells(
  chunks: string[],
  anchors: LatticeAnchors2D,
  rules: LatticeRules,
  config: ChunkConfig
): GlyphCell[] {
  const { layout, gridColumns = 3, primaryScale = 0.5, overlapFactor = 0.1 } = config;

  // Calculate positions based on layout
  let positions: { x: number; y: number; width: number; height: number }[];

  switch (layout) {
    case 'hierarchical':
      positions = calculateHierarchicalLayout(chunks.length, primaryScale);
      break;
    case 'mosaic':
      positions = calculateMosaicLayout(chunks.length, overlapFactor);
      break;
    case 'grid':
    default:
      positions = calculateGridLayout(chunks.length, gridColumns);
      break;
  }

  // Create cells with encoded paths
  return chunks.map((text, i) => {
    const path = encodeText(text, anchors, rules);
    const pos = positions[i] || { x: 0, y: 0, width: 0.2, height: 0.2 };

    return {
      id: `cell-${i}`,
      text,
      path,
      x: pos.x,
      y: pos.y,
      width: pos.width,
      height: pos.height,
      rotation: 0,
      zIndex: layout === 'mosaic' ? chunks.length - i : i,
      opacity: layout === 'mosaic' ? 0.85 + (i / chunks.length) * 0.15 : 1,
    };
  });
}

/**
 * Encode long text into a composite glyph structure
 */
export function encodeComposite(
  text: string,
  anchors: LatticeAnchors2D,
  rules: LatticeRules,
  style: any,
  latticeKey: string,
  config: Partial<ChunkConfig> = {}
): CompositeGlyph {
  const fullConfig = { ...DEFAULT_CHUNK_CONFIG, ...config };
  const chunks = chunkText(text, fullConfig.chunkSize);
  const cells = createGlyphCells(chunks, anchors, rules, fullConfig);

  const metadata: CompositeMetadata = {
    fullText: normalizeText(text),
    totalChunks: chunks.length,
    chunkSize: fullConfig.chunkSize,
    layout: fullConfig.layout,
    latticeKey,
    timestamp: new Date().toISOString(),
    hash: generateCompositeHash(text),
  };

  return {
    version: '1.0',
    metadata,
    cells,
    anchors,
    style,
  };
}

// QBC 3D Encoder - Text to 3D Glyph Path

import {
  LatticeAnchors3D,
  EncodedPath3D,
  PathEvent3D,
} from './types3d';
import { LatticeRules } from './types';

/**
 * Extended character set for 3D lattices (343 positions in 7x7x7)
 * Supports:
 * - A-Z Latin uppercase (26)
 * - 0-9 digits (10)
 * - Space (1)
 * - Punctuation and special characters (~50)
 * - Cyrillic uppercase (33)
 * - Greek uppercase (24)
 * - Hebrew (22)
 * - Common CJK/Mandarin radicals (50+)
 * - Arabic numerals extended
 */

// Latin A-Z
const LATIN = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

// Digits
const DIGITS = '0123456789';

// Punctuation and special characters
const PUNCTUATION = ' !@#$%^&*()-_=+[]{}|\\:;\'"<>,.?/`~';

// Cyrillic uppercase (Russian, Ukrainian, etc.)
const CYRILLIC = 'ÐÐÐÐÐÐÐÐÐÐÐÐÐÐÐÐÐ Ð¡Ð¢Ð£Ð¤Ð¥Ð¦Ð§Ð¨Ð©ÐªÐ«Ð¬Ð­Ð®Ð¯';

// Greek uppercase
const GREEK = 'ÎÎÎÎÎÎÎÎÎÎÎÎÎÎÎÎ Î¡Î£Î¤Î¥Î¦Î§Î¨Î©';

// Hebrew
const HEBREW = '×××××××××××××× ×¡×¢×¤×¦×§×¨×©×ª';

// Common CJK radicals and characters (simplified for encoding)
const CJK = 'ä¸äºä¸åäºå­ä¸å«ä¹åäººå¤§ä¸­å°ä¸ä¸å·¦å³å¤©å°æ¥æç«æ°´æ¨éåå¿æå£';

// Arabic letters (basic)
const ARABIC = 'Ø§Ø¨ØªØ«Ø¬Ø­Ø®Ø¯Ø°Ø±Ø²Ø³Ø´ØµØ¶Ø·Ø¸Ø¹ØºÙÙÙÙÙÙÙÙÙ';

// Combined extended character set
export const EXTENDED_CHARS = (
  LATIN +
  DIGITS +
  PUNCTUATION +
  CYRILLIC +
  GREEK +
  HEBREW +
  CJK +
  ARABIC
).split('');

// Character set lookup for fast checking
const EXTENDED_CHARS_SET = new Set(EXTENDED_CHARS);

/**
 * Normalize text for extended character set
 * Converts Latin to uppercase, preserves other scripts
 */
export function normalizeText3D(text: string): string {
  return text
    .split('')
    .map(char => {
      // Uppercase Latin characters
      const upper = char.toUpperCase();
      if (EXTENDED_CHARS_SET.has(upper)) return upper;
      // Keep original if it's in extended set
      if (EXTENDED_CHARS_SET.has(char)) return char;
      // Filter out unsupported chars
      return '';
    })
    .filter(Boolean)
    .join('');
}

/**
 * Calculate tick direction in 3D space
 */
function getTickDirection3D(
  x: number, y: number, z: number,
  prevX: number, prevY: number, prevZ: number
): { dx: number; dy: number; dz: number } {
  // Vector from previous to current
  const vx = x - prevX;
  const vy = y - prevY;
  const vz = z - prevZ;

  // Find a perpendicular vector
  // Use cross product with up vector (0,1,0) or right (1,0,0) if parallel
  let px, py, pz;
  if (Math.abs(vy) > 0.9) {
    // Mostly vertical, use right vector
    px = vz;
    py = 0;
    pz = -vx;
  } else {
    // Cross with up vector
    px = vy * 1 - vz * 0;
    py = vz * 0 - vx * 1;
    pz = vx * 0 - vy * 0;
  }

  // Normalize
  const len = Math.hypot(px, py, pz) || 1;
  return { dx: px / len, dy: py / len, dz: pz / len };
}

/**
 * Encode text into a 3D glyph path
 */
export function encodeText3D(
  text: string,
  anchors: LatticeAnchors3D,
  rules: LatticeRules
): EncodedPath3D {
  const normalized = normalizeText3D(text);
  const chars = normalized.split('');

  const events: PathEvent3D[] = [];
  const visitCounts: Record<string, number> = {};
  const visitedChars: string[] = [];

  let penX = 0, penY = 0, penZ = 0;
  let prevPenX = 0, prevPenY = 0, prevPenZ = 0;
  let prevChar: string | null = null;

  for (let i = 0; i < chars.length; i++) {
    const char = chars[i];
    const anchor = anchors[char];

    if (!anchor) continue;

    const [x, y, z] = anchor;
    const isFirstChar = events.length === 0;
    const isRepeat = prevChar === char || visitCounts[char] > 0;

    visitCounts[char] = (visitCounts[char] || 0) + 1;
    visitedChars.push(char);

    if (isFirstChar) {
      events.push({ type: 'move', char, x, y, z });
      penX = x; penY = y; penZ = z;
      prevPenX = x; prevPenY = y; prevPenZ = z;
    } else if (isRepeat && rules.enableTick) {
      // Draw line to anchor
      events.push({ type: 'line', char, x, y, z });

      // Calculate incoming direction
      const inFromX = (penX === x && penY === y && penZ === z) ? prevPenX : penX;
      const inFromY = (penX === x && penY === y && penZ === z) ? prevPenY : penY;
      const inFromZ = (penX === x && penY === y && penZ === z) ? prevPenZ : penZ;

      prevPenX = inFromX; prevPenY = inFromY; prevPenZ = inFromZ;
      penX = x; penY = y; penZ = z;

      // Calculate tick
      const tickLength = rules.tickLengthFactor || 0.08;
      const { dx, dy, dz } = getTickDirection3D(x, y, z, inFromX, inFromY, inFromZ);
      const tickEndX = x + dx * tickLength;
      const tickEndY = y + dy * tickLength;
      const tickEndZ = z + dz * tickLength;

      events.push({
        type: 'tick',
        char,
        x, y, z,
        tickEndX, tickEndY, tickEndZ,
      });

      prevPenX = x; prevPenY = y; prevPenZ = z;
      penX = tickEndX; penY = tickEndY; penZ = tickEndZ;
    } else {
      events.push({ type: 'line', char, x, y, z });
      prevPenX = penX; prevPenY = penY; prevPenZ = penZ;
      penX = x; penY = y; penZ = z;
    }

    prevChar = char;
  }

  return { events, visitedChars, visitCounts };
}

