              <Button size="sm" variant="outline" className="flex-1">
                <Eye className="h-4 w-4 mr-1" />
                View
              </Button>
              <Button size="sm" variant="outline">
                <Download className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="border-b">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <Link to="/qbc" className="flex items-center gap-2 text-lg font-semibold">
            <Home className="h-5 w-5" />
            Rosetta Library
          </Link>
          <nav className="flex gap-2">
            <Button variant="ghost" asChild>
              <Link to="/qbc/simulator">Simulator</Link>
            </Button>
          </nav>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6">
        <div className="mb-6">
          <h1 className="text-3xl font-bold mb-2">Rosetta Library</h1>
          <p className="text-muted-foreground">
            Explore the universal dictionary of QBC glyphs
          </p>
        </div>

        <div className="mb-6">
          <div className="relative max-w-md">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search glyphs..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
            />
          </div>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab}>
          <TabsList className="mb-6">
            <TabsTrigger value="public">Public Library</TabsTrigger>
            <TabsTrigger value="mine">My Library</TabsTrigger>
          </TabsList>

          <TabsContent value="public">
            {loadingPublic ? (
              <div className="text-center py-12 text-muted-foreground">
                Loading glyphs...
